const TheirMessage = ({ lastMessage, message }) => {
    const isFirsMessageByUser = !lastMessage || lastMessage.sender.username !== message.sender.usermame;

    return (
        <div className="message-row">
            {isFirsMessageByUser && (
                <div
                    className="message-avatar"
                    style={{
                        backgroundImage: message.sender && 'url(${message.sender.avatar})'
                    }}
                />
            )}

            {message.attachment && message.attachment.length > 0
                ? (
                    <img
                        src={message.attachments[0].file}
                        alt="message-attechment"
                        className="message-image"
                        style={{ marginLeft: isFirsMessageByUser ? '4px' : '48px' }}
                    />
                )
                : (
                    <div className="message" style={{
                        float: 'left', backgroundColor: '#CABCDC',
                        marginLeft: isFirsMessageByUser ? '4px' : '48px'
                    }}>
                        {message.text}
                    </div>
                )}

        </div>
    );
};
export default TheirMessage;
